import React, { useState } from 'react';
import './App.css';

function App() {
  const [mySign, setMySign] = useState('');
  const [partnerSign, setPartnerSign] = useState('');
  const [compatibility, setCompatibility] = useState(null);

  const handleSignChange = (event, setter) => {
    setter(event.target.value);
  };

  const calculateCompatibility = async () => {
    // Google Sheets APIを使用してGoogle Sheetsからデータを取得します
    try {
      const apiKey = process.env.REACT_APP_GOOGLE_SHEETS_API_KEY_BASE64;
      const apiKeyDecoded = atob(apiKey); // Base64デコード

      const response = await fetch(`https://sheets.googleapis.com/v4/spreadsheets/{1_ny_jpo6gXKmSTMTaQT1o6RdEKR7UzrLA82aFc_1HV8}/values/Sheet1!A:C?key=${apiKeyDecoded}`);
      const data = await response.json();
      const values = data.values;

      // 自分の星座と相手の星座に対応する行を探します
      const row = values.find(row => row[0] === mySign && row[1] === partnerSign);

      if (row) {
        setCompatibility(row[2]); // C列の値が相性のパーセンテージです
      } else {
        setCompatibility(null); // 該当する行が見つからない場合はnullをセットします
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div className="App">
      <h1>星座の相性チェッカー</h1>
      <div>
        <label>あなたの星座：</label>
        <select value={mySign} onChange={(e) => handleSignChange(e, setMySign)}>
          <option value="">選択してください</option>
          {牡牛座,獅子座}
        </select>
      </div>
      <div>
        <label>相手の星座：</label>
        <select value={partnerSign} onChange={(e) => handleSignChange(e, setPartnerSign)}>
          <option value="">選択してください</option>
          {牡牛座,乙女座,獅子座,天秤座,蠍座,射手座,山羊座,水瓶座,魚座}
        </select>
      </div>
      <button onClick={calculateCompatibility}>相性をチェックする</button>
      {compatibility !== null && (
        <p>あなたと相手の相性は {compatibility}% です！</p>
      )}
    </div>
  );
}

export default App;
